mapper.select:
SELECT BS.BSHID, BS.ROUID, BS.BUSID, BS.DEPARTUREDATE, C.COMNAME, B.BUSGRADE, BS.DURMIN, BS.ADULTFARE, BS.STUFARE, BS.CHILDFARE, BS.ARRIVALDATE, BS.REMAINSEATS, B.BUSSEATS FROM busschedule BS JOIN BUS B ON BS.BUSID = B.BUSID JOIN COMPANY C ON B.COMID = C.COMID JOIN ROUTE R ON BS.ROUID = R.ROUID JOIN arrival A ON A.ARRID = R.ARRID JOIN departure D ON D.DEPID = R.DEPID JOIN REGION RGD ON D.REGID = RGD.REGID JOIN REGION RGA ON A.REGID = RGA.REGID
RGD.REGID = #{deprId} AND RGA.REGID = #{arrId}
AND BS.DEPARTUREDATE = TO_TIMESTAMP(#{deprDtm}, 'YYYYMMDD HH24:MI')
AND TRUNC(BS.DEPARTUREDATE) = TO_DATE(#{deprDtm}, 'YYYYMMDD')
AND 1=1
AND B.BUSGRADE = #{busClsCd}
